import * as React from 'react';
import { PropertyControls, ControlType } from 'framer';
import * as icons from 'react-icons/fa';

// Define type of property
interface Props {
  iconName: string;
  color: string;
  width: number;
  height: number;
}

export class FontAwesome extends React.Component<Props> {
  static defaultProps = {
    iconName: 'FaHome',
    color: '#aaa',
  };

  static propertyControls: PropertyControls = {
    iconName: { type: ControlType.String, title: 'Icon' },
    color: {
      type: ControlType.Color,
      title: 'Color',
    },
  };

  render() {
    const Icon = icons[this.props.iconName] || icons['FaBeer'];
    const minSize = Math.min(this.props.width, this.props.height);
    return <Icon color={this.props.color} size={minSize} />;
  }
}
